// /frontend/src/App.js

import React from 'react';
import UploadForm from './components/UploadForm';
// 1. We import the ChatWindow component we just created.
import ChatWindow from './components/ChatWindow';

// This is our main App component.
function App() {
  return (
    <div className="min-h-screen bg-gray-50 flex flex-col items-center p-4 sm:p-6 lg:p-8">
      <div className="w-full max-w-6xl mx-auto">
        
        {/* Header Section */}
        <header className="text-center mb-10">
          <h1 className="text-3xl sm:text-4xl font-bold text-gray-800">
            AI X-Ray Diagnosis Assistant
          </h1>
          <p className="mt-2 text-md text-gray-600">
            Upload an X-ray image to get an AI-powered analysis and ask questions.
          </p>
        </header>

        {/* Main Content Area */}
        <main className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          
          {/* Left Column: Upload and Results */}
          <div className="bg-white p-6 rounded-lg shadow-md flex flex-col">
            <div>
              <h2 className="text-2xl font-semibold text-gray-700 mb-4">1. Upload Image</h2>
              <UploadForm />
            </div>
            <div className="mt-8">
              <h2 className="text-2xl font-semibold text-gray-700 mb-4">2. View Analysis</h2>
              <div className="h-64 bg-gray-50 border border-gray-200 rounded-lg flex items-center justify-center">
                <p className="text-gray-500">Analysis results will appear here.</p>
              </div>
            </div>
          </div>

          {/* Right Column: Chat */}
          <div className="bg-white p-6 rounded-lg shadow-md flex flex-col">
            <h2 className="text-2xl font-semibold text-gray-700 mb-4">3. Ask Questions</h2>
            {/* 2. We replace the old placeholder div with our new ChatWindow component. */}
            <div className="flex-grow">
              <ChatWindow />
            </div>
          </div>

        </main>
      </div>
    </div>
  );
}

export default App;
