// /frontend/src/components/ResultsDisplay.js

import React from 'react';

// This component will receive the analysis results as 'props' from the main App component.
// For now, we'll use placeholder data until we connect the backend.
function ResultsDisplay({ analysisResult, uploadedImage }) {
  return (
    <div className="h-full bg-gray-50 border border-gray-200 rounded-lg p-4 flex flex-col lg:flex-row gap-4">
      
      {/* Image Preview Section */}
      <div className="w-full lg:w-1/2 h-48 lg:h-full bg-gray-200 rounded-md flex items-center justify-center">
        {uploadedImage ? (
          // If an image has been uploaded, create a temporary URL for it and display it.
          <img 
            src={URL.createObjectURL(uploadedImage)} 
            alt="Uploaded X-Ray" 
            className="max-h-full max-w-full object-contain rounded-md" 
          />
        ) : (
          // If no image is uploaded yet, show a placeholder.
          <p className="text-gray-500 text-sm">Image preview will appear here</p>
        )}
      </div>

      {/* Analysis Text Section */}
      <div className="w-full lg:w-1/2 overflow-y-auto">
        <h3 className="text-lg font-semibold text-gray-800 mb-2">AI Analysis</h3>
        {analysisResult ? (
          // If there are analysis results, display them.
          <div className="text-sm text-gray-700 whitespace-pre-wrap">
            {analysisResult}
          </div>
        ) : (
          // If there are no results yet, show a placeholder message.
          <p className="text-gray-500 text-sm">
            Upload an image and click "Analyze" to see the results.
          </p>
        )}
      </div>

    </div>
  );
}

export default ResultsDisplay;
