// /frontend/src/App.js

import React, { useState } from 'react';
import UploadForm from './components/UploadForm';
import ChatWindow from './components/ChatWindow';
import ResultsDisplay from './components/ResultsDisplay';

function App() {
  const [uploadedImage, setUploadedImage] = useState(null);
  const [analysisResult, setAnalysisResult] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // This function now sends the file to the backend.
  const handleAnalyze = async (file) => {
    setUploadedImage(file);
    setIsLoading(true);
    setAnalysisResult('Analyzing, please wait...');

    // 1. Create a FormData object to send the file.
    // This is the standard way to send files via a fetch request.
    const formData = new FormData();
    formData.append('file', file);

    try {
      // 2. Use the 'fetch' API to send a POST request to our backend endpoint.
      const response = await fetch('http://localhost:5000/api/analyze', {
        method: 'POST',
        body: formData,
      });

      // 3. Check if the request was successful.
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      // 4. Parse the JSON response from the backend.
      const data = await response.json();

      // 5. Format the received data into a user-friendly string.
      const formattedResult = `AI Analysis for: ${data.fileName}\n\n- Finding: ${data.findings}\n- Confidence: ${data.confidence}\n- Note: ${data.disclaimer}`;
      
      setAnalysisResult(formattedResult);

    } catch (error) {
      // If there's an error (e.g., the backend is not running), display an error message.
      console.error("There was a problem with the fetch operation:", error);
      setAnalysisResult(`Error analyzing image. Please ensure the backend server is running and try again. Details: ${error.message}`);
    } finally {
      // 6. Set loading to false, whether the request succeeded or failed.
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col items-center p-4 sm:p-6 lg:p-8">
      <div className="w-full max-w-6xl mx-auto">
        
        <header className="text-center mb-10">
          <h1 className="text-3xl sm:text-4xl font-bold text-gray-800">
            AI X-Ray Diagnosis Assistant
          </h1>
          <p className="mt-2 text-md text-gray-600">
            Upload an X-ray image to get an AI-powered analysis and ask questions.
          </p>
        </header>

        <main className="grid grid-cols-1 lg-grid-cols-2 gap-8">
          
          <div className="bg-white p-6 rounded-lg shadow-md flex flex-col">
            <div>
              <h2 className="text-2xl font-semibold text-gray-700 mb-4">1. Upload Image</h2>
              <UploadForm onAnalyze={handleAnalyze} isLoading={isLoading} />
            </div>
            <div className="mt-8 flex-grow flex flex-col">
              <h2 className="text-2xl font-semibold text-gray-700 mb-4">2. View Analysis</h2>
              <div className="flex-grow">
                <ResultsDisplay 
                  uploadedImage={uploadedImage} 
                  analysisResult={analysisResult} 
                />
              </div>
            </div>
          </div>

          <div className="bg-white p-6 rounded-lg shadow-md flex flex-col">
            <h2 className="text-2xl font-semibold text-gray-700 mb-4">3. Ask Questions</h2>
            <div className="flex-grow">
              <ChatWindow />
            </div>
          </div>

        </main>
      </div>
    </div>
  );
}

export default App;
