// /frontend/src/components/ResultsDisplay.js

import React from 'react';

function ResultsDisplay({ analysisResult, uploadedImage, isLoading }) {
  // A simple helper to parse the analysis text and make it look nice
  const renderFindings = (text) => {
    if (!text || typeof text !== 'string') return null;
    return text.split('\n').map((line, index) => (
      <span key={index}>
        {line}
        <br />
      </span>
    ));
  };

  return (
    <div className="h-full bg-gray-50 border border-gray-200 rounded-lg p-4 flex flex-col lg:flex-row gap-4 transition-all duration-300">
      
      {/* Image Preview Section */}
      <div className="w-full lg:w-1/2 h-48 lg:h-full bg-gray-200 rounded-md flex items-center justify-center overflow-hidden">
        {uploadedImage ? (
          <img 
            src={URL.createObjectURL(uploadedImage)} 
            alt="Uploaded X-Ray" 
            className="max-h-full max-w-full object-contain rounded-md" 
          />
        ) : (
          <p className="text-gray-500 text-sm px-4 text-center">Image preview will appear here</p>
        )}
      </div>

      {/* Analysis Text Section */}
      <div className="w-full lg:w-1/2 overflow-y-auto pr-2">
        <h3 className="text-lg font-semibold text-gray-800 mb-2">AI Analysis</h3>
        
        {/* Show a progress bar while loading */}
        {isLoading && (
          <div>
            <p className="text-gray-600 mb-2">Analyzing, please wait...</p>
            <div className="w-full bg-gray-200 rounded-full h-2.5">
              <div className="bg-blue-600 h-2.5 rounded-full animate-pulse"></div>
            </div>
          </div>
        )}

        {/* Show the formatted results when not loading and results are available */}
        {!isLoading && analysisResult && (
          <div className="text-sm text-gray-700 space-y-3">
            {renderFindings(analysisResult.findings)}
            {analysisResult.confidence && <p><strong>Confidence:</strong> {analysisResult.confidence}</p>}
            <p className="text-xs text-gray-500 italic mt-4">{analysisResult.disclaimer}</p>
          </div>
        )}

        {/* Show the initial placeholder text */}
        {!isLoading && !analysisResult && (
          <p className="text-gray-500 text-sm">
            Upload an image and click "Analyze" to see the results.
          </p>
        )}
      </div>
    </div>
  );
}

export default ResultsDisplay;
